INSERT INTO "podcast" VALUES (0, 'ورود اژدها و بیانیه دزدهای دریایی', 'رادیو گیک شماره صفر', NULL, NULL, 'http://jadi.net/2012/03/radio-geek-pirates-ejdeha/', 'http://jadi.net/audio/jadi.net_radio-geek_000-dragon-pirates.mp3', 0, 0);
INSERT INTO "podcast" VALUES (1, 'سینگولاریتی', 'قسمت صفرم رادیو گیک با استقبال غیرقابل باوری برای من روبرو شد (بیشتر از دوهزار و پونصد تا دانلود فقط از سایت خودم) و این باعث شد که احتمالا رادیو رو ادامه بدیم', NULL, NULL, 'http://jadi.net/2012/03/radio-geek-001-singularity/', 'http://jadi.net/audio/jadi-net_radio-geek_001_singularity.mp3', 0, 0);
INSERT INTO "podcast" VALUES (2, 'از فضا و فراسوی فضا', 'آماده باشین که اگر برنامه رو گوش می دین دوبار توش قراره سر پا بایستیم به احترام انسان هایی که از ما با شرف تر هستن و یکبار چشم هامون رو به زمین می دوزیم از بی حیایی توروالدز. با ما باشین…', NULL, NULL, 'http://jadi.net/2012/03/radio-geek-2-space-and-beyond/', 'http://jadi.net/audio/jadi-net_radio-geek_002_space-and-beyond.mp3', 0, 0);
INSERT INTO "podcast" VALUES (3, 'غول بازار مکاره', 'رادیو غول بازار مکاره که با یک گاز سر مرغ ها رو از بدنشون جدا می کنه و هر آشغالی که مردم جلوش بریزن رو می خوره.. رادیو گیک. این هفته با امنیت، دزدهای دریایی. مصاحبه با یک هکر ایرانی و …شراب قرمزی که آهن رو به زانو در می یاره', NULL, NULL, 'http://jadi.net/2012/04/radio-geek-3-googhle-bazare-makkare/', 'http://jadi.net/audio/jadi-net_radio-geek_003_ghoole_bazare_makkare.mp3', 0, 0);
INSERT INTO "podcast" VALUES (4, 'رادیو گیک – شماره چهار – اشک ماهی ها', 'در شماره چهارم رادیوگیک از معلمی صحبت می کنیم که به خاطر ندادن پسورد فیسبوکش از مدرسه اخراج شد، نگاهی می ندازیم به انگلستان که داره تلاش می کنه آدم ها رو بیشتر و بیشتر زیر نظر دولتش در بیاره و روشی کشف می کنیم برای بالا بردن تمرکز و دقت آدم ها در یک چشم به هم زدن و شیپور افتخارات رو – اینبار آرومتر – می زنیم به احترام برد پیت. ', NULL, NULL, 'http://jadi.net/2012/04/radio-geek-4-ashke-maahiha-dar-berke/', 'http://jadi.net/audio/jadi-net_radio-geek_004_ashke-maahi-haa.mp3', 0, 0);
INSERT INTO "podcast" VALUES (5, 'رادیو گیک – شماره پنجم – هکرها و اساتید', 'این شماره رو بهروز وثوقی با خوندن حکم آق حسینی شروع می کنه تا ما جرات کنیم اسم بخش هکرها رو به بخش اساتید تغییر بدیم که مجبور نشده باشیم واقعیت های جهان بیرون رو توی قالب های خودمون فرو کنیم. نگاهی کوتاهی به صنعتی داریم که به جای اسمش خودمون بوق سانسور می زنیم و بعد می ریم سراغ هکرها و هکرها. ', NULL, NULL, 'http://jadi.net/2012/04/radoi-geek-5-hackerha-asatid/', 'http://jadi.net/audio/jadi-net_radio-geek_005_hackers_vs_masters.mp3', 0, 0);
INSERT INTO "podcast" VALUES (6, 'رادیو گیک - شماره شش - ویران شود این خانه که میخانه ندارد', 'در این شماره که می شد اسمش باشه گیک های خندان، اولش دلمون می گیره از این شهری که وقتی یک میکروفون خوب یو اس بی می خوای توش بخری هیچ جاش پیدا نمی شه، از سانسورچی های اینترنت و شرکت های بزرگ محدود کننده اش و از پتنت ترول هایی که برای سودشون تکنولوژی رو خفه می کن..', NULL, NULL, 'http://jadi.net/2012/04/radio-geek-hindi-zahra/', 'http://jadi.net/audio/jadi-net_radio-geek_006_shahre_viran.mp3', 0, 0);
INSERT INTO "podcast" VALUES (7, 'رادیو گیک – شماره هفتم – دو هفته سکوت', 'بعد از دو هفته سکوت، این هفته رادیوگیک به شیوه سابق در پنجاه و یک دقیقه با شما است تا از خیانت خانم سیری به جابز شروع کنه و بعد برسه به جشن عروسی ای که فرامرز آصف زینت بخش اونه. در ادامه برنامه زیردریایی ما رو به اعماقی می بره پر داستان. خاطرات یک کاربر ایرانسل رو می شنویم و خوشحال می شیم از حقوق دیجیتالی که توی هلند مردم به دست آوردن', NULL, NULL, 'http://jadi.net/2012/05/radio-geek-2-hafte-sokoot/', 'http://jadi.net/audio/jadi-net_radio-geek_007_do_hafte_sokoot.mp3', 0, 0);
INSERT INTO "podcast" VALUES (8, 'رادیو گیک – شماره هشتم – ترک مکانیکی', 'در این شماره با زیلوفون پینگ می کنیم ، تله پورت می کنیم، ادعای پولدار شدن گوگل با ۹ خط کد اوراکل رو بررسی می کنیم و به افتخار پرویز شهریاری به سه دقیقه شعر شاملو گوش می دیم. آخرش هم فراموش نمی کنیم که گیک ضد ضرب است اما ضد ضرب درست.', NULL, NULL, 'http://jadi.net/2012/05/radio-geek-8-tork-mekaniki/', 'http://jadi.net/audio/jadi-net_radio-geek_008_mechanical_turk.mp3', 0, 0);
INSERT INTO "podcast" VALUES (9, 'رادیو گیک ، شماره نهم ، راز شادی', 'با شعر هالو درباره مرد و نامرد شروع می کنیم که هر کدوم یک چیزی رو در ایران ملی کردن و بعد می ریم سراغ اخبار تکنولوژی، بررسی های عمیق تر و یک مقاله طولانی اما زیبا و مهم با ترجمه هما مداح و صدای امیر و حسام. با ما باشین، با رادیو گیک.', NULL, NULL, 'http://jadi.net/2012/05/radio-geek-9-be-happy/', 'http://jadi.net/audio/jadi-net_radio-geek_009_how-to-be-happy.mp3', 0, 0);
INSERT INTO "podcast" VALUES (10, 'رادیو گیک – شماره ده – فارنهایت ۴۵۱', 'گیک هایی که دوست دارن تقاطع تکنولوژی و جامعه رو ببینن در این شماره از اخبار تا اعماق پیش می رن و آخرش به افتخار یک نویسنده عالی، انگلیسی تمرین می کنن. شماره دهم رادیو گیک در انتظار شنیده شدن است', NULL, NULL, 'http://jadi.net/2012/06/radio-geek-010-451/', 'http://jadi.net/audio/jadi-net_radio-geek_010_ray_451.mp3', 0, 0);
INSERT INTO "podcast" VALUES (11, 'رادیو گیک – شماره یازدهم – ناکس های ناتو', 'رادیو گیک. از کارمند ناتوی اپل تا تلاش برای هک کردن ارتش آزاد سوریه و بازار زیرو دی هایی که میتونیم بهش بگیم «امنیت برای یک درصد» و رسیدن به این سوال پایه ای که آیا کامپیوترها باید آزادی بیان داشته باشن؟
INSERT INTO "podcast" VALUES (12, 'رادیو گیک. شماره دوازدهم: معلومه خب شلیته!', 'رادیویی برای کسانی که تکنولوژی براشون فقط تلاش دائمی برای خوندن و حفظ اینکه فلان مدل فلان چیز فرقش با فلان مدل بهمان چیز چیه و تو بازار چنده و شایعه اینکه قراره کی مدل فلانترش بیاد نیست و ترجیح می‌دن یک پله عمیق تر بشن و تو تقاطع تکنولوژی و جامعه، دغدغه‌های انسانی‌شون رو مطرح کنن. رادیو گیک برای گیک های سرگردان در تقاطع جامعه و تکنولوژی.', NULL, NULL, 'http://jadi.net/2012/07/radio-geek-12-maloome-khob-shalite/', 'http://jadi.net/audio/jadi-net_radio-geek_012_maloome-khob-shalite.mp3', 0, 0);
INSERT INTO "podcast" VALUES (13, 'رادیو گیک. شماره سیزدهم : دشنه', 'رادیویی برای کسانی که تکنولوژی براشون فقط تلاش دائمی برای خوندن و حفظ اینکه فلان مدل فلان چیز فرقش با فلان مدل بهمان چیز چیه و تو بازار چنده و شایعه اینکه قراره کی مدل فلانترش بیاد نیست و ترجیح می‌دن یک پله عمیق تر بشن و تو تقاطع تکنولوژی و جامعه، دغدغه‌های انسانی‌شون رو مطرح کنن. رادیو گیک برای گیک های سرگردان در تقاطع جامعه و تکنولوژی.', NULL, NULL, 'http://jadi.net/2012/07/radio-geek-13-deshneh/', 'http://jadi.net/audio/jadi-net_radio-geek_013_deshneh.mp3', 0, 0);
INSERT INTO "podcast" VALUES (14, 'رادیو گیک. شماره چهاردهم، نفوذ تکنولوژی', 'به نظر خودم این یکی از رادیوگیک های خوب است. پر از انسان های باحال و اخباری از سطح و عمق دنیای گیک ها. با بخش آخری که توش اریک اشمیت در مورد نفوذ تکنولوژی و فروریختن فایروال بزرگ چین حرف می زنه. توضیح: ضبط قبل از زلزله آذرباییجان بوده. تسلیت می گم و امیدوارم ', NULL, NULL, 'http://jadi.net/2012/08/radio-geek-nofooze-technology/', 'http://jadi.net/audio/jadi-net_radio-geek_014_nofooze_ettelaat.mp3', 0, 0);
INSERT INTO "podcast" VALUES (15, 'رادیو گیک. شماره پانزدهم فریاد بکشید', 'توی رادیو گیک این شماره، در تقاطع تکنولوژی و جامعه با همه فریاد می کشیم. با داریوش که نخواست .. و عاشق آس تهران شد آسانژ و پوسی رایوتی که توی کلیسا فریاد کشید علیه رییس بزرگ. هفته هفته فریاده. با ما باشین که قبل از رفتن سراغ ماهی سیاه کوچیک، نه فقط بک دور', NULL, NULL, 'http://jadi.net/2012/08/radio-geek-faryad-bekeshid/', 'http://jadi.net/audio/jadi-net_radio-geek_015_faryad_bekeshid.mp3', 0, 0);
INSERT INTO "podcast" VALUES (16, 'رادیو گیک، شماره شانزدهم، رویای فریدمن', 'در شماره های ویژه رادیو گیک، اخبار رو سریعتر مرور کنیم و زود می ریم سراغ توضیح مفصل یک مفهوم. مفهوم مفصل این شماره؟ پولی به اسم بیت کوین ! جدیدترین پول جهان.. و البته عجیب ترین! آماده باشین که می خوایم دولت ها، اقتصاد، هکرها، منابع طلا، رمزگزاری، هش و شبکه های پیر تو', NULL, NULL, 'http://jadi.net/2012/09/radio-geek-royaye-fridman/', 'http://jadi.net/audio/jadi-net_radio-geek_016_friedmans_dream.mp3', 0, 0);
INSERT INTO "podcast" VALUES (17, 'رادیو گیک. شماره هفدهم، هش هش هش، به افتخار بچه های لجستیک', 'رادیوگیک این هفته، شماره ای پر از هش،‌پر از شکستن رمزها و پر از زامبی. رادیو گیک شماره هفدهم، برای گیک هایی می خوان ببینن آیا فیسبوک دنبال کنترل جمعیت می ره یا نه ! رادیویی برای کسانی که تکنولوژی براشون فقط تلاش دائمی برای خوندن و حفظ اینکه فلان مدل فلان چیز فرقش با', NULL, NULL, 'http://jadi.net/2012/10/radio-geek-hash-hash-hash/', 'http://jadi.net/audio/jadi-net_radio-geek_017_hash-hash-hash.mp3', 0, 0);
INSERT INTO "podcast" VALUES (18, 'رادیو گیک. شماره هجدهم، داستان کوتاه «هریسون برگرن» از «کرت وونه‌گات»', 'یک شماره ویژه داریم. کش نمی دم جریان رو چون توی رادیو پنج دقیقه در موردش حرف زدم (: برای بیست دقیقه گوش بدین به یک داستان کوتاه از یکی از بهترین علمی تخیلی نویس‌ها، کرت یا کورت وونه‌گات. نویسنده‌ای که با سلاخ خانه شماره پنج و از اون بالاتر گهواره گربه یکی از نویسنده ', NULL, NULL, 'http://jadi.net/2012/10/radiogeek-18-kurt-vonnegut-harrison-bergeron/', 'http://jadi.net/audio/jadi-net_radio-geek_018_Kurt-Vonnegut_Harrison-Bergeron.mp3', 0, 0);
INSERT INTO "podcast" VALUES (19, 'رادیو گیک. شماره نوزدهم، شما امنیت ندارید', 'شماره نوزدهم شماره ای پر از رمز و راز و تقریبا همه خبرها مربوط به امنیت. از رازهای جنگ جهانی و فراماسونری قرن هجده تا زیر نظر گرفتن دانش آموزها و زنان عربستان. با رادیو گیک باشیم چون می خوایم به همه نیوزلندی ها اینترنت پر سرعت رایگان بدیم. رادیویی برای کسانی که تکنولوژی', NULL, NULL, 'http://jadi.net/2012/11/radiogeek-19-shoma-amn-nistid/', 'http://jadi.net/audio/jadi-net_radio-geek_019_you-are-not-safe.mp3', 0, 0);
INSERT INTO "podcast" VALUES (20, 'رادیو گیک. شماره بیستم، تا قبر آ آ آ آ', 'مهمون ویژه این شماره: ۱پزشک مشهور به علیرضا مجیدی. رادیویی برای کسانی که تکنولوژی براشون فقط تلاش دائمی برای خوندن و حفظ اینکه فلان مدل فلان چیز فرقش با فلان مدل بهمان چیز چیه و تو بازار چنده و شایعه اینکه قراره کی مدل فلانترش بیاد نیست و ترجیح می‌دن یک پله عمیق تر بشن', NULL, NULL, 'http://jadi.net/2012/12/radio-geek-20-doroogh-chera-a-a-a-a/', 'http://jadi.net/audio/jadi-net_radio-geek_020_ta_ghabr_a_a_a_a.mp3', 0, 0);
INSERT INTO "podcast" VALUES (21, 'رادیو گیک. شماره بیست و یک، صدای یک دست', 'رادیویی برای کسانی که تکنولوژی براشون فقط تلاش دائمی برای خوندن و حفظ اینکه فلان مدل فلان چیز فرقش با فلان مدل بهمان چیز چیه و تو بازار چنده و شایعه اینکه قراره کی مدل فلانترش بیاد نیست و ترجیح می‌دن یک پله عمیق تر بشن و تو تقاطع تکنولوژی و جامعه، دغدغه‌های انسانی‌شون رو', NULL, NULL, 'http://jadi.net/2013/01/radiogeek-21-sedaye-yek-dast/', 'http://jadi.net/audio/jadi-net_radio-geek_021_sound_of_one_hand.mp3', 0, 0);
INSERT INTO "podcast" VALUES (22, 'رادیو گیک. شماره بیست و دو، دوست دختر مصنوعی', 'در شماره بیست و دومم رادیو گیک در خدمت شما هستیم با عنوانی به جذابی: دوست دختر مصنوعی. در این شماره راه حلی کشف می کنیم برای اینکه شما دوست پسر / دختر ندارین، نگاهی می کنیم به دستگیری هکری که توی فهرست ده تایی اف بی آی بود و می بینیم که بعضی مدرسه', NULL, NULL, 'http://jadi.net/2013/02/radio-geek-22-doost-dokhtar-masnooei/', 'http://jadi.net/audio/jadi-net_radio-geek_022_doost-dokhtar-masnooei.mp3', 0, 0);
INSERT INTO "podcast" VALUES (23, 'رادیو گیک. شماره بیست و سه، تجربه قهوه ای تیره', 'به رادیو گیک شماره ۲۳ خوش اومدین. توی این شماره با اخبار دنیای گیک‌ها شروع می کنیم و بعد به اعماق مغز می ریم تا ببینیم چطوری دانشمندها تونستن احساسات و دستورهای عضلانی دو موش رو به هم وصل کنن تا یکیشون به عنوان کامپیوتر اون یکی کار کنه. آخر برنامه رو هم با دنیایی', NULL, NULL, 'http://jadi.net/2013/03/radio-geek-23-tajrobeye-ghavei-tire/', 'http://jadi.net/audio/jadi-net_radio-geek_023_ghahvei_tire.mp3', 0, 0);
INSERT INTO "podcast" VALUES (24, 'رادیو گیک. شماره بیست و چهار: ایستگاه‌های اعداد یا همون جاسوس های دولت های ضاله و پدهای یکبار مصرفشون', 'بعد از مدتی، یک شماره ویژه داریم برای گیک‌هایی که جاسوسی دوست دارن، برای گیک‌هایی که می دونن رادیو چیه و موج گیرنده رادیوشون رو می چرخونن و به صدای جاسوس های سراسر جهان گوش می دن' , NULL, NULL, 'http://jadi2.undo.it/2013/04/radiogeek-24-numbers-station/', 'http://jadi2.undo.it/audio/jadi-net_radio-geek_024_numbers-stations.mp3', 0, 0);
INSERT INTO "podcast" VALUES (25, 'رادیو گیک. شماره بیست و پنج: شش ماه دیگه همه شادیم', 'رادیویی برای کسانی که تکنولوژی براشون فقط تلاش دائمی برای خوندن و حفظ اینکه فلان مدل بهمان چیز فرقش با اون یکی مدل اون یکی کارخونه چیه و تو بازار چنده و شایعه اینکه قراره کی مدل مثلا خفن‌ترش بیاد نیست و ترجیح می‌دن یک پله عمیق تر بشن و تو تقاطع تکنولوژی و جامعه، دغدغه‌های انسانی‌شون رو مطرح کنن. رادیو گیک برای گیک های سرگردان در تقاطع جامعه و تکنولوژی',NULL, NULL, 'http://jadi2.undo.it/2013/04/radio-geek-shish-maah-dige-hame-shadim/', 'http://jadi2.undo.it/audio/jadi-net_radio-geek_025_shish-maah-dige-hame-shadim.mp3', 0, 0);